import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [prompts, setPrompts] = useState([]);
  const [selectedPrompt, setSelectedPrompt] = useState("");
  const [selectedImages, setSelectedImages] = useState([]);
  const [output, setOutput] = useState("");
  const [inputText, setInputText] = useState("");
  const [images, setImages] = useState([]);

  useEffect(() => {
    // Fetch saved prompts from the backend
    axios
      .get("http://127.0.0.1:5000/get-prompts")
      .then((response) => {
        setPrompts(response.data.prompts || []);
      })
      .catch((error) => {
        console.error("Error fetching prompts:", error);
        alert("Unable to load saved prompts.");
      });

    // Fetch image filenames dynamically from the backend
    axios
      .get("http://127.0.0.1:5000/images")
      .then((response) => {
        const filenames = response.data; // Assume response.data is an array of filenames
        const imageList = filenames.map((filename, index) => ({
          id: index + 1,
          src: `http://127.0.0.1:5000/images/${filename}`, // Construct the full image URL
          alt: filename,
        }));
        setImages(imageList);
      })
      .catch((error) => {
        console.error("Error fetching images:", error);
        alert("Unable to load images.");
      });
  }, []);

  const handlePromptClick = (prompt) => {
    setSelectedPrompt(prompt);
  };

  const handleImageSelect = (image) => {
    setSelectedImages((prev) =>
      prev.includes(image) ? prev.filter((img) => img !== image) : [...prev, image]
    );
  };

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleAddPrompt = () => {
    if (inputText.trim() === "") return;

    const newPrompt = inputText.trim();

    // Save the prompt to the backend
    axios
      .post("http://127.0.0.1:5000/save-prompt", { prompt: newPrompt })
      .then(() => {
        setPrompts([...prompts, newPrompt]);
        setInputText(""); // Clear the input field
      })
      .catch((error) => {
        console.error("Error saving prompt:", error);
        alert("Unable to save the prompt.");
      });
  };

  const handleFileInput = (e) => {
    // Ensure selected images are valid File objects
    const files = Array.from(e.target.files); // Convert FileList to Array
    setSelectedImages(files); // Store File objects
  };

  const handleSubmit = async () => {
    if (!selectedPrompt) {
      alert("Please provide a prompt.");
      return;
    }

    if (selectedImages.length === 0) {
      alert("Please upload at least one image.");
      return;
    }

    // Convert File objects to Base64 strings
    const encodedImages = await Promise.all(
      selectedImages.map((file) => {
        return new Promise((resolve, reject) => {
          const reader = new FileReader();
          reader.onloadend = () => {
            resolve({
              filename: file.name,
              data: reader.result, // Base64 encoded string
            });
          };
          reader.onerror = reject;
          reader.readAsDataURL(file); // Ensure this works with valid File objects
        });
      })
    );

    try {
      const response = await axios.post("http://127.0.0.1:5000/process", {
        prompt: selectedPrompt,
        images: encodedImages,
      });
      const result = response.data.results;
      setOutput(result); // Display results
    } catch (error) {
      console.error("Error processing request:", error);
      alert("An error occurred. Please try again.");
    }
  };
  return (
    <div className="app">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Prompts</h2>
        {prompts.length === 0 ? (
          <p>No prompts added yet.</p>
        ) : (
          <ul>
            {prompts.map((prompt, index) => (
              <li
                key={index}
                className={selectedPrompt === prompt ? "active" : ""}
                onClick={() => handlePromptClick(prompt)}
              >
                {prompt}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Main Content */}
      <div className="main-content">
        <div className="content">
          <h2>Selected Prompt</h2>
          <p>{selectedPrompt || "No prompt selected."}</p>

          {/* Image Selection */}
          <h3>Select Images</h3>
          <div className="image-grid">
            {images.map((image) => (
              <div
                key={image.id}
                className={`image-item ${
                  selectedImages.includes(image.src) ? "selected" : ""
                }`}
                onClick={() => handleImageSelect(image.src)}
              >
                <img src={image.src} alt={image.alt} />
              </div>
            ))}
          </div>

          {/* Submit */}
          <button onClick={handleSubmit} className="submit-btn">
            Go ➡️
          </button>

          {/* Output */}
          <h3>Output</h3>
          <textarea
            value={output}
            readOnly
            className="output-box"
            placeholder="Output will appear here..."
          ></textarea>
        </div>

        {/* Input Box at Bottom */}
        <div className="input-box">
          <input
            type="text"
            placeholder="Enter a new prompt here..."
            value={inputText}
            onChange={handleInputChange}
            onKeyDown={(e) => e.key === "Enter" && handleAddPrompt()}
          />
          <button onClick={handleAddPrompt}>Add</button>
        </div>
      </div>
    </div>
  );
};

export default App;
