import React, { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [prompts, setPrompts] = useState([]);
  const [selectedPrompt, setSelectedPrompt] = useState("");
  const [images, setImages] = useState([]);
  const [selectedImages, setSelectedImages] = useState([]);
  const [output, setOutput] = useState("");
  const [newPrompt, setNewPrompt] = useState("");

  useEffect(() => {
    // Fetch images from the backend
    axios.get("http://127.0.0.1:5000/images").then((response) => {
      setImages(
        response.data.map((filename, index) => ({
          id: index + 1,
          src: `http://127.0.0.1:5000/images/${filename}`,
          alt: filename,
        }))
      );
    });

    // Load saved prompts from the backend
    axios.get("http://127.0.0.1:5000/prompts").then((response) => {
      setPrompts(response.data);
    });
  }, []);

  const handleAddPrompt = () => {
    if (!newPrompt) return;

    // Save the new prompt
    axios
      .post("http://127.0.0.1:5000/prompts", { prompt: newPrompt })
      .then(() => {
        setPrompts([...prompts, newPrompt]);
        setNewPrompt("");
      })
      .catch((error) => {
        console.error("Error saving prompt:", error);
      });
  };

  const handleImageSelect = (image) => {
    setSelectedImages((prevSelected) =>
      prevSelected.some((img) => img.id === image.id)
        ? prevSelected.filter((img) => img.id !== image.id)
        : [...prevSelected, image]
    );
  };

  const handleProcess = () => {
    if (!selectedPrompt) {
      alert("Please select a prompt.");
      return;
    }

    if (selectedImages.length === 0) {
      alert("Please select at least one image.");
      return;
    }

    const payload = {
      prompt: selectedPrompt,
      images: selectedImages.map((img) => img.src),
    };

    axios
      .post("http://127.0.0.1:5000/process", payload)
      .then((response) => {
        setOutput(response.data.output || "No output received.");
      })
      .catch((error) => {
        console.error("Error processing data:", error);
        alert("An error occurred. Please try again.");
      });
  };

  return (
    <div className="App">
      <div className="sidebar">
        <h3>Prompts</h3>
        {prompts.length === 0 ? (
          <p>No prompts added yet.</p>
        ) : (
          <ul>
            {prompts.map((prompt, index) => (
              <li
                key={index}
                className={selectedPrompt === prompt ? "selected" : ""}
                onClick={() => setSelectedPrompt(prompt)}
              >
                {prompt}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="main">
        <h3>Selected Prompt</h3>
        <p>{selectedPrompt || "No prompt selected."}</p>

        <h3>Select Images</h3>
        <div className="image-grid">
          {images.map((image) => (
            <img
              key={image.id}
              src={image.src}
              alt={image.alt}
              className={selectedImages.some((img) => img.id === image.id) ? "selected" : ""}
              onClick={() => handleImageSelect(image)}
            />
          ))}
        </div>

        <button className="go-button" onClick={handleProcess}>
          Go ➡️
        </button>

        <h3>Output</h3>
        <textarea
          value={output}
          readOnly
          placeholder="Output will appear here..."
          className="output-box"
        ></textarea>

        <div className="new-prompt">
          <input
            type="text"
            value={newPrompt}
            onChange={(e) => setNewPrompt(e.target.value)}
            placeholder="Enter a new prompt..."
          />
          <button onClick={handleAddPrompt}>Add</button>
        </div>
      </div>
    </div>
  );
};

export default App;
