import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [prompts, setPrompts] = useState([]);
  const [selectedPrompt, setSelectedPrompt] = useState("");
  const [selectedImages, setSelectedImages] = useState([]);
  const [output, setOutput] = useState([]);
  const [inputText, setInputText] = useState("");
  const [images, setImages] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false); // State to track submission status

  useEffect(() => {
    // Fetch saved prompts from the backend
    axios
      .get("http://127.0.0.1:5000/get-prompts")
      .then((response) => {
        setPrompts(response.data.prompts || []);
      })
      .catch((error) => {
        console.error("Error fetching prompts:", error);
        alert("Unable to load saved prompts.");
      });

    // Fetch image filenames dynamically from the backend
    axios
      .get("http://127.0.0.1:5000/images")
      .then((response) => {
        const filenames = response.data; // Array of filenames
        const imageList = filenames.map((filename, index) => ({
          id: index + 1,
          src: `http://127.0.0.1:5000/images/${filename}`, // Full image URL
          filename,
        }));
        setImages(imageList);
      })
      .catch((error) => {
        console.error("Error fetching images:", error);
        alert("Unable to load images.");
      });
  }, []);

  const handlePromptClick = (prompt) => {
    setSelectedPrompt(prompt);
  };

  const handleImageSelect = (image) => {
    setSelectedImages((prev) =>
      prev.includes(image) ? prev.filter((img) => img !== image) : [...prev, image]
    );
  };

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleAddPrompt = () => {
    if (inputText.trim() === "") return;

    const newPrompt = inputText.trim();

    // Save the prompt to the backend
    axios
      .post("http://127.0.0.1:5000/save-prompt", { prompt: newPrompt })
      .then(() => {
        setPrompts([...prompts, newPrompt]);
        setSelectedPrompt(newPrompt); // Set the newly added prompt as selected
        setInputText(""); // Clear the input field
      })
      .catch((error) => {
        console.error("Error saving prompt:", error);
        alert("Unable to save the prompt.");
      });
  };

  const handleSubmit = () => {
    // Ensure the Go button disables during submission
    setIsSubmitting(true);

    // If no prompt is selected and no input text is provided, show an error
    if (!selectedPrompt) {
      alert("Please select or add a prompt before submitting.");
      setIsSubmitting(false);
      return;
    }

    if (selectedImages.length === 0) {
      alert("Please select at least one image.");
      setIsSubmitting(false);
      return;
    }

    const selectedFilenames = selectedImages.map((img) =>
      images.find((image) => image.src === img)?.filename
    );

    axios
      .post("http://127.0.0.1:5000/process", {
        prompt: selectedPrompt,
        images: selectedFilenames,
      })
      .then((response) => {
        setOutput(response.data.results || []);
        setIsSubmitting(false); // Enable Go button after submission completes
      })
      .catch((error) => {
        console.error("Error processing request:", error);
        alert("An error occurred while processing your request.");
        setIsSubmitting(false); // Enable Go button if there's an error
      });
  };

  const handleGoClick = () => {
    if (inputText.trim()) {
      handleAddPrompt(); // First, add the prompt if there's input text
    }
    handleSubmit(); // Proceed with submit after adding the prompt
  };

  const handleEnterKey = (e) => {
    if (e.key === "Enter") {
      handleAddPrompt(); // Trigger the add prompt functionality when Enter is pressed
    }
  };

  return (
    <div className="app">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Prompts</h2>
        {prompts.length === 0 ? (
          <p>No prompts added yet.</p>
        ) : (
          <ul>
            {prompts.map((prompt, index) => (
              <li
                key={index}
                className={selectedPrompt === prompt ? "active" : ""}
                onClick={() => handlePromptClick(prompt)}
              >
                {prompt}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Main Content */}
      <div className="main-content">
        {/* Input Box and Go Button */}
        <div className="input-box">
          <input
            type="text"
            placeholder="Enter a new prompt here..."
            value={inputText}
            onChange={handleInputChange}
            onKeyDown={handleEnterKey} // Trigger the functionality when Enter is pressed
          />
          {/* Go button should now trigger handleSubmit */}
          <button onClick={handleGoClick} disabled={isSubmitting}>
            {isSubmitting ? "Processing..." : "Go ➡️"}
          </button>
        </div>

        {/* Output Box */}
        <div className="output-box">
          {output.length > 0 ? (
            output.map((res, idx) => (
              <div key={idx} className="output-item">
                <h4>Filename: {res.filename}</h4>
                <p>Description: {res.description}</p>
              </div>
            ))
          ) : (
            <p>Output will appear here...</p>
          )}
        </div>

        {/* Active Prompt */}
        <div className="selected-prompt">
          <h2>Selected Prompt</h2>
          <p>{selectedPrompt || "No prompt selected."}</p>
        </div>

        {/* Image Selection */}
        <h3>Select Images</h3>
        <div className="image-grid">
          {images.map((image) => (
            <div
              key={image.id}
              className={`image-item ${
                selectedImages.includes(image.src) ? "selected" : ""
              }`}
              onClick={() => handleImageSelect(image.src)}
            >
              <img src={image.src} alt={image.filename} />
            </div>
          ))}
        </div>

        {/* Submit */}
        <button onClick={handleSubmit} className="submit-btn" disabled={isSubmitting}>
          {isSubmitting ? "Processing..." : "Go ➡️"}
        </button>
      </div>
    </div>
  );
};

export default App;
